package array;

public class LengthDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] classGroup = new String[5];
		classGroup[0] = "JS";
		System.out.println(classGroup.length);
		classGroup[1] = "EJ";
		System.out.println(classGroup.length);
		classGroup[2] = "CJ";
		System.out.println(classGroup.length);
		classGroup[3] = "SM";
		System.out.println(classGroup.length);
		classGroup[4] = "JM";
		System.out.println(classGroup.length);
		
	}

}
